products = []
product_id = 1

for category_id, product_list in products_by_category.items():
    for name, description in product_list:
        days_ago = random.randint(30, 365)
        created = datetime.now() - timedelta(days=days_ago)
        
        products.append({
            "id": product_id,
            "name": name,
            "description": description,
            "price": random.randint(50, 15000),
            "currency": "TRY",
            "sku": product_id,
            "isActive": random.random() > 0.15,
            "categoryId": category_id,
            "createdAt": created.strftime("%Y-%m-%dT%H:%M:%SZ"),
            "updatedAt": created.strftime("%Y-%m-%dT%H:%M:%SZ")
        })
        product_id += 1

with open("seed-data/products.json", "w", encoding="utf-8") as f:
    json.dump(products, f, ensure_ascii=False, indent=2)

